"use strict";function verifyNumbers(e,n){return 0<=e&&0<=n}window.onload=function(){document.getElementById("calcular").addEventListener("click",function(){var e=parseFloat(document.getElementById("n1").value),n=parseFloat(document.getElementById("n2").value);if(verifyNumbers(e,n)){var t=e+n;document.getElementById("resultado").innerHTML=t}else document.getElementById("resultado").innerHTML="Está faltando número aí, espertinho!"})};